sp_widget.client_script:
function($scope,$rootScope,spUtil) {
		/* widget controller */
		var c = this;

		$rootScope.$on('selectNote', function(event,data) {
			c.server.get({
				action: 'getNote',
				data: data
			}).then(function(r) {

				console.log('slog submitted');
			});


			setTimeout(function(){


spUtil.update($scope);
console.log('refreshed');

}, 3000);


		});
	}

